import React from "react";

const Navbar = () => {
  
  return (
    <nav className="bg-white shadow-md p-4 sticky top-0 z-50">
    <div className="container mx-auto flex justify-between items-center">
      {/* Logo */}
      <div className="text-2xl font-bold text-blue-600">FreelanceHub</div>

      {/* Desktop Menu */}
      <ul className="hidden md:flex space-x-6 text-gray-700">
        <li className="hover:text-blue-600 cursor-pointer">Home</li>
        <li className="hover:text-blue-600 cursor-pointer">Browse Jobs</li>
        <li className="hover:text-blue-600 cursor-pointer">Post a Job</li>
        <li className="hover:text-blue-600 cursor-pointer">About</li>
        <li className="hover:text-blue-600 cursor-pointer">Contact</li>
      </ul>

      {/* Search Bar */}
      <div className="hidden md:flex items-center bg-gray-100 px-3 py-1 rounded-md">
        <Search size={18} className="text-gray-500" />
        <input type="text" placeholder="Search..." className="bg-transparent outline-none ml-2" />
      </div>

      {/* Auth Buttons */}
      <div className="hidden md:flex space-x-4">
        <Button variant="ghost">Sign In</Button>
        <Button variant="default">Sign Up</Button>
      </div>

      {/* Mobile Menu Button */}
      <button className="md:hidden" onClick={() => setIsOpen(!isOpen)}>
        {isOpen ? <X size={28} /> : <Menu size={28} />}
      </button>
    </div>

    {/* Mobile Menu */}
    {isOpen && (
      <ul className="md:hidden flex flex-col items-center bg-white shadow-md py-4 space-y-4 text-gray-700">
        <li className="hover:text-blue-600 cursor-pointer">Home</li>
        <li className="hover:text-blue-600 cursor-pointer">Browse Jobs</li>
        <li className="hover:text-blue-600 cursor-pointer">Post a Job</li>
        <li className="hover:text-blue-600 cursor-pointer">About</li>
        <li className="hover:text-blue-600 cursor-pointer">Contact</li>
        <div className="flex flex-col space-y-2">
          <Button variant="ghost">Sign In</Button>
          <Button variant="default">Sign Up</Button>
        </div>
      </ul>
    )}
  </nav>
  );
};

export default Navbar;
